#include <stdio.h>
#include <time.h>
#include "big_integer.h"


int main() {

    BigInt a = big_integer_create("");

    BigInt b = big_integer_create("");

    BigInt aa = big_integer_create("8837364663456");
    BigInt bb = big_integer_create("93837873");

    clock_t start, end;
    double cpu_time_used;

    start = clock(); // Start the timer
    BigInt c = big_integer_add(a, b);
    end = clock(); // Stop the timer
    printf("carry: %d-", c.d);
    for (int i = c.n - 1; i >= 0; i--) {
        printf("%d", (int)c.rep[i]);
    }
    printf("\n");

    cpu_time_used = ((double)(end - start)) / CLOCKS_PER_SEC; // Calculate the elapsed time
    printf("Time taken: %f seconds\n", cpu_time_used);

    start = clock(); // Start the timer
    BigInt cc = big_integer_substract(aa, bb);
    end = clock(); // Stop the timer
    printf("carry: %d-", cc.d);
    for (int i = cc.n - 1; i >= 0; i--) {
        printf("%d", (int)cc.rep[i]);
    }
    printf("\n");

    cpu_time_used = ((double)(end - start)) / CLOCKS_PER_SEC; // Calculate the elapsed time
    printf("Time taken: %f seconds\n", cpu_time_used);

    return 0;
}
